'use strict'

const u = require('universalify').fromPromise
const jsonFile = require('fs-extra/lib/json/jsonfile')

jsonFile.outputJson = u(require('fs-extra/lib/json/output-json'))
jsonFile.outputJsonSync = require('fs-extra/lib/json/output-json-sync')
// aliases
jsonFile.outputJSON = jsonFile.outputJson
jsonFile.outputJSONSync = jsonFile.outputJsonSync
jsonFile.writeJSON = jsonFile.writeJson
